
public class A1p7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char n='/';
		float a=15, b=5, c=0;
		switch (n)
		{
		case '+':
			c=a+b;
				System.out.println("The value of a+b= "+c);
			break;
		case '-':
			c=a-b;
				System.out.println("The value of a-b= "+c);
			break;
		case '*':
			c=a*b;
				System.out.println("The value of a*b= "+c);
			break;
		case '/':
			c=a/b;
				System.out.println("The value of a/b= "+c);
			break;
		default:
			System.out.println("Error!");
			
			
		
		}
		
		

	}

}
